function getCookie(e){if(cookie_info=document.cookie,cookie_info){list=cookie_info.replace(/;\s/g,";").split(";");for(var o=0;o<list.length;o++)if(item=list[o].split("="),item[0]==e)return oldCookie=item[1],JSON.parse(oldCookie);return[]}return[]}function setCookie(e,o,i){var t=new Date;t.setTime(t.getTime()+24*i*60*60*1e3),document.cookie=e+"="+o+";Expires="+t+";"}function removeCookie(e){setCookie(e,"",-1)}